package bin;

import java.util.Scanner;

public class Bin {
    public static void main(String[] args) {
        // TODO code application logic here
        double notas[][] = new double[4][3];
        double promedio[] = new double[4];
        Scanner teclado = new Scanner (System.in);
        
        for (int f = 0; f < notas.length; f++){
            System.out.println("Ingrese las 3 notas del alumno");
            
            for(int c = 0; c < notas[0].length; c++){notas[f][c] = teclado.nextDouble();}
        }
        double total;
        
        for (int f = 0; f < notas.length; f++){
            total = 0;
            
            for(int c = 0; c < notas[0].length; c++){total = total + notas[f][c];}
            promedio[f] = total / notas[0].length;
        }
        for (int f = 0; f < notas.length; f++){
            System.out.println("Las notas del alumno NÂ° " + f + " son:");
            
            for(int c = 0; c < notas[0].length; c++){System.out.println(notas[f][c]);}
            System.out.println("El promedio fue de: " + promedio[f]);
        }
    }
}