function getDay(n){
    // body...
    let day = ""
    if(day === 1){day = "Lunes"}
    if(day === 2){day = "Martes"}
    if(day === 3){day = "Miércoles"}
    if(day === 4){day = "Jueves"}
    if(day === 5){day = "Viernes"}
    if(day === 6){day = "Sábado"}
    if(day === 7){day = "Domingo"}
    if(day === ""){throw new Error("Out of range")}
    return day;
}